declare module '*.png'
declare module '*.jpg'
declare module '*.webp'
declare module '*.svg'
declare module '*.woff'
declare module '*.woff2'
declare module 'react-router-scroll-4'
declare module 'react-tree-walker'
declare module 'emotion-server'
declare module 'create-react-context/lib/implementation'
declare module '*.json'
declare module '*.ttf'
declare module 'serviceworker-webpack-plugin/lib/runtime'
