declare module '@react-pdf/renderer' {
  type BaseProps = {
    style?: any
    debug?: boolean
  }
  type Comp<T> = React.SFC<BaseProps & T>

  export const Document: React.SFC<{
    title?: string
    author?: string
    subject?: string
    keywords?: string
    creator?: string
    producer?: string
  }>

  export const Page: Comp<{
    size?: string | [number, number]
    orientation?: 'portrait' | 'ladscape'
    wrap?: boolean
  }>
  export const View: Comp<{
    wrap?: boolean
  }>
  export const Image: Comp<{
    src?: string
  }>
  export const Text: Comp<{
    wrap?: boolean
    render?: Function
  }>
  export const Link: Comp<{
    src?: string
    wrap?: boolean
  }>
  export const Font: {
    register(url: string, opts: { family: string }): void
  }
}

interface NodeRequire {
  resolveWeak(mod: string): number
}
